import React, { useEffect, useState } from "react";
import { Navigate } from "react-router-dom";

export default function ProtectedRoute({ children, allowedRoles }) {
  const [loading, setLoading] = useState(true);
  const [authorized, setAuthorized] = useState(false);

  useEffect(() => {
    const checkAuth = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/auth/check-auth", {
          credentials: "include", // important to include cookie
        });
        if (!res.ok) throw new Error("Unauthorized");
        const data = await res.json();

        // Check if user role is allowed
        if (allowedRoles.includes(data.role)) setAuthorized(true);
      } catch {
        setAuthorized(false);
      } finally {
        setLoading(false);
      }
    };

    checkAuth();
  }, [allowedRoles]);

  if (loading) return <p className="text-white">Checking authentication...</p>;

  return authorized ? children : <Navigate to="/my-dashboard/login" replace />;
}
